import React, { useEffect, useState } from "react";
import { UnlockingContainerNotification } from "../UnlockingContainerNotification/UnlockingContainerNotification";
import ItemLine from "../ItemLine/ItemLine";
import { ItemChance, ItemStatrakChance } from "../../helpers/chance";
import { CaseOpeningConfirmation } from "../CaseOpeningConfirmation/CaseOpeningConfirmation";
import ReceivedItemModal from "../ReceivedItemModal/ReceivedItemModal";
import { useRouter } from "next/router";
import { getRandomInt } from "../../helpers/random";
import { containerType } from "../../helpers/container-type";
import { itemType } from "../../helpers/item-type";
import CaseOpeningAudio from "../CaseOpeningAudio/CaseOpeningAudio";

export const CaseOpeningSec = (props) => {
  const [line, setLine] = useState(null);
  const [openingInProgress, setOpeningInProgress] = useState(false);
  const [receivedItemModalVisible, setReceivedItemModalVisible] = useState(
    false
  );
  const [receivedItem, setReceivedItem] = useState(null);
  const router = useRouter();
  const itemLineRef = React.createRef();

  useEffect(() => {
    setLine(generateLine());
  }, []);

  const generateItem = () => {
    let actItem;
    switch (props.containerType) {
      case containerType[0]:
        actItem = selectRandomItem(getRandom());
        break;
      case containerType[1]:
        actItem = selectRandomItem(getRandomSouvenir());
        break;
      default:
        actItem = selectRandomItem(getRandom());
        break;
    }
    setReceivedItem(actItem);
    return actItem;
  };

  const generateLine = () => {
    let elements = [];
    let actItem = generateItem();
    for (let index = 0; index < 30; index++) {
      if (index === 25) {
        elements.push(actItem);
      } else {
        let item;
        switch (props.containerType) {
          case containerType[0]:
            item = selectRandomItem(getRandom());
            break;
          case containerType[1]:
            item = selectRandomItem(getRandomSouvenir());
            break;
          default:
            item = selectRandomItem(getRandom());
            break;
        }
        elements.push(item);
      }
    }
    return <ItemLine ref={itemLineRef} items={elements} />;
  };

  const getRandomSouvenir = () => {
    const ran = Math.random();
    if (
      props.items.some((e) => e.type == itemType.GREY) &&
      ran < ItemChance.GREY
    )
      return itemType.GREY;
    else if (
      props.items.some((e) => e.type == itemType.LIGHTBLUE) &&
      ran < ItemChance.LIGHTBLUE
    )
      return itemType.LIGHTBLUE;
    else if (
      props.items.some((e) => e.type == itemType.BLUE) &&
      ran < ItemChance.BLUE
    )
      return itemType.BLUE;
    else if (
      props.items.some((e) => e.type == itemType.PURPLE) &&
      ran < ItemChance.PURPLE
    )
      return itemType.PURPLE;
    else if (
      props.items.some((e) => e.type == itemType.PINK) &&
      ran < ItemChance.PINK
    )
      return itemType.PINK;
    else if (
      props.items.some((e) => e.type == itemType.RED) &&
      ran < ItemChance.RED
    )
      return itemType.RED;
    else if (
      props.items.some((e) => e.type == itemType.GOLD) &&
      ran < ItemChance.GOLD
    )
      return itemType.GOLD;
  };

  const getRandom = () => {
    const ran = Math.random();
    if (ran < ItemChance.BLUE) return itemType.BLUE;
    else if (ran < ItemChance.PURPLE) return itemType.PURPLE;
    else if (ran < ItemChance.PINK) return itemType.PINK;
    else if (ran < ItemChance.RED) return itemType.RED;
    else if (ran < ItemChance.GOLD) return itemType.GOLD;
  };

  const getRandomStatrak = (type) => {
    const ran = Math.random();
    if (ran < ItemStatrakChance.BLUE && type == itemType.BLUE) return true;
    else if (ran < ItemStatrakChance.PURPLE && type == itemType.PURPLE)
      return true;
    else if (ran < ItemStatrakChance.PINK && type == itemType.PINK) return true;
    else if (ran < ItemStatrakChance.RED && type == itemType.RED) return true;
    else if (ran < ItemStatrakChance.GOLD && type == itemType.GOLD) return true;
  };

  const selectRandomItem = (type) => {
    let isStatrak;
    if (!props.isSouvenier) {
      isStatrak = getRandomStatrak(type);
    } else {
      isStatrak = false;
    }
    if (type === itemType.GOLD) {
      const ran = getRandomInt(0, props.specialItems.length);
      return {
        ...props.specialItems[ran],
        isStatrak,
      };
    } else {
      const allGroupItems = [...props.items.filter((val) => val.type === type)];
      const ran = getRandomInt(0, allGroupItems.length);
      return { ...allGroupItems[ran], isStatrak };
    }
  };
  const openCase = () => {
    setOpeningInProgress(true);
    setTimeout(() => {
      setReceivedItemModalVisible(true);
    }, 8100);
  };
  const onReceivedItemConfirmation = () => {
    router.push("/");
  };
  const onTryAgainBtnClick = () => {
    unmountComponentAtNode(itemLineRef.current);
    setReceivedItemModalVisible(false);
    setOpeningInProgress(false);
    setLine(generateLine());
    setOpeningInProgress(true);
    setTimeout(() => {
      setReceivedItemModalVisible(true);
    }, 8100);
  };
  return (
    <>
      <div className='case-opening-sec'>
        {openingInProgress && <CaseOpeningAudio />}
        {receivedItemModalVisible && (
          <ReceivedItemModal
            title={receivedItem.title}
            isStatrak={receivedItem.isStatrak}
            type={receivedItem.type}
            image={receivedItem.image}
            onTryAgainBtnClick={() => onTryAgainBtnClick()}
            onSubmit={() => onReceivedItemConfirmation()}
          />
        )}

        <div className='head-wrap'>
          {!openingInProgress && (
            <CaseOpeningConfirmation
              image={props.caseImage}
              title={props.caseTitle}
              onSubmit={() => openCase()}
            />
          )}
          {openingInProgress && (
            <>
              <div className='case-opener-bg'>
                <hr className='middle-line' />
                <div className='case-opener'>{line}</div>
              </div>
            </>
          )}
        </div>
        {openingInProgress && <UnlockingContainerNotification />}
      </div>

      <style jsx>
        {`
          .case-opening-sec {
            width: 100%;
            height: 50%;
            padding-top: 2rem;
            background: #121b24;
            box-shadow: 0px 0px 11px 1px rgba(0, 0, 0, 0.75);
          }

          .case-opening-sec .head-wrap {
            width: 102%;
            transform: translateX(-1%);
            background: #10161b;
            box-shadow: 0px 0px 11px 1px rgba(0, 0, 0, 0.75);
            height: 54%;
            display: flex;
            align-items: center;
            justify-content: center;
          }

          .case-opening-sec .head-wrap .case-opener-bg {
            width: 400px;
            background: black;
            position: relative;
            padding: 16px 2px;
          }

          .case-opening-sec .head-wrap .case-opener-bg .case-opener {
            width: 100%;
            height: 102px;
            overflow: hidden;
            position: relative;
          }

          .middle-line {
            background: #605337;
            height: 100%;
            position: absolute;
            left: 50%;
            width: 3px;
            transform: translateX(-50%);
            top: 0;
            margin: 0;
            z-index: 1;
            border: none;
          }
        `}
      </style>
    </>
  );
};
