import React, { Component } from "react";

export default class CaseOpeningAudio extends Component {
  constructor(props) {
    super(props);
    this.rollSound = React.createRef();
    this.playOpeningSound();
  }

  playOpeningSound = () => {
    this.rollSound.current.loop = true;
    this.rollSound.current.playbackRate = 1.5;
    this.rollSound.current.play();
    setTimeout(() => {
      console.log(2);
    }, 4000);
    setTimeout(() => {
      this.rollSound.current.playbackRate = 1.2;
    }, 2000);
    setTimeout(() => {
      this.rollSound.current.playbackRate = 0.8;
    }, 2000);
    this.rollSound.current.pause();
  };

  render() {
    return (
      <>
        <audio ref={this.rollSound} id='caseOpeningAudioRoll'>
          <source src='/sound/roll.mp3' type='audio/mp3' />
        </audio>
        <audio id='caseOpeningAudioBlueDrop'>
          <source src='/sound/drop2.mp3' type='audio/mp3' />
        </audio>
        <audio id='caseOpeningAudioPurpleDrop'>
          <source src='/sound/drop3.mp3' type='audio/mp3' />
        </audio>
        <audio id='caseOpeningAudioPinkDrop'>
          <source src='/sound/drop4.mp3' type='audio/mp3' />
        </audio>
        <audio id='caseOpeningAudioRedDrop'>
          <source src='/sound/drop5.mp3' type='audio/mp3' />
        </audio>
      </>
    );
  }
}
