export default class Storage {
  constructor() {
    this.identifier = "receivedItems";
  }

  static saveReceivedItem = (item) => {
    const curValue =
      localStorage.getItem(identifier) != null
        ? JSON.parse(localStorage.getItem(identifier))
        : [];
    const valToStore = JSON.stringify(Array.of(...curValue, item));
    localStorage.setItem(identifier, valToStore);
  };

  static selectAll = () => {
    return JSON.parse(localStorage.getItem(identifier));
  };
}
