import React, { Component } from "react";
import "./item-card.module.scss";

export default class ItemCard extends Component {
  constructor(props) {
    super(props);
    switch (this.props.type) {
      case 1:
        this.state = {
          gunCardBorderName: "item-card--blue"
        };
        break;
      case 2:
        this.state = {
          gunCardBorderName: "item-card--purple"
        };
        break;
      case 3:
        this.state = {
          gunCardBorderName: "item-card--pink"
        };
        break;
      case 4:
        this.state = {
          gunCardBorderName: "item-card--red"
        };
        break;
      default:
        break;
    }
  }

  render() {
    return (
      <div className={`item-card ${this.state.gunCardBorderName}`}>
        <img src={this.props.image} alt='' />
      </div>
    );
  }
}
