import React from "react";
import Link from "next/link";

export const HeaderAnchor = (props) => {
  return (
    <>
      <Link href={props.to}>
        <a className={`header-btn ${props.bordered && `header-btn__bordered`}`}>
          {props.title}
        </a>
      </Link>
      <style jsx>
        {`
          .header-btn {
            height: 100%;
            width: 200px;
            background: none;
            outline: none;
            font-weight: 500;
            font-size: 1.03rem;
            color: white;
            display: flex;
            align-items: center;
            justify-content: center;
            text-decoration: none;
            box-sizing: content-box;
          }
          .header-btn__bordered {
            border-right: 3px solid rgba(0, 0, 0, 0.4);
          }
          .header-btn:hover {
            background: rgba(255, 255, 255, 0.4);
          }
        `}
      </style>
    </>
  );
};
