import React, { Component } from "react";

export default class CaseOpeningAudio extends Component {
  constructor(props) {
    super(props);
    this.rollSound = React.createRef();
    this.rollSound2 = React.createRef();
    this.openSound = React.createRef();
  }

  componentDidMount() {
    this.playOpeningSound();
    this.rollSound.current.volume = 0.3;
    this.rollSound2.current.volume = 0.3;
    this.openSound.current.volume = 0.3;
  }

  playOpeningSound = () => {
    this.openSound.current.play();
    this.openSound.current.playbackRate = 1.2;

    this.rollSound.current.loop = true;
    this.rollSound.current.playbackRate = 1.1;

    this.rollSound2.current.loop = true;
    this.rollSound2.current.playbackRate = 1.1;

    this.rollSound.current.play();
    setTimeout(() => {
      this.rollSound2.current.play();
    }, 1000);

    setTimeout(() => {
      this.rollSound2.current.pause();
      this.rollSound.current.playbackRate = 1.05;
    }, 4000);
    setTimeout(() => {
      this.rollSound.current.playbackRate = 0.9;
    }, 6000);
    setTimeout(() => {
      this.rollSound.current.pause();
    }, 8000);
  };

  render() {
    return (
      <>
        <audio preload='auto' ref={this.openSound} id='caseOpeningAudioOpen'>
          <source src='/sound/open.mp3' type='audio/mp3' />
        </audio>
        <audio preload='auto' ref={this.rollSound} id='caseOpeningAudioRoll'>
          <source src='/sound/roll.mp3' type='audio/mp3' />
        </audio>
        <audio preload='auto' ref={this.rollSound2} id='caseOpeningAudioRoll'>
          <source src='/sound/roll.mp3' type='audio/mp3' />
        </audio>
        <audio preload='auto' id='caseOpeningAudioBlueDrop'>
          <source src='/sound/drop2.mp3' type='audio/mp3' />
        </audio>
        <audio preload='auto' id='caseOpeningAudioPurpleDrop'>
          <source src='/sound/drop3.mp3' type='audio/mp3' />
        </audio>
        <audio preload='auto' id='caseOpeningAudioPinkDrop'>
          <source src='/sound/drop4.mp3' type='audio/mp3' />
        </audio>
        <audio preload='auto' id='caseOpeningAudioRedDrop'>
          <source src='/sound/drop5.mp3' type='audio/mp3' />
        </audio>
      </>
    );
  }
}
