import React, { useState, useEffect } from "react";
import { useRouter } from "next/router";
import ItemLine from "../../Components/ItemLine/ItemLine";
import Showcase from "../../Components/Showcase/Showcase";
import CaseOpeningSec from "../../Components/CaseOpeningSec/CaseOpeningSec";
import cases from "../../public/cases.json";

const Case = () => {
  const router = useRouter();
  const [items, setItems] = useState(null);

  useEffect(() => {
    if (router && router.query && router.query.id) {
      setItems(cases.find(value => value.id === +router.query.id).items);
    }
  }, [router]);

  return (
    <>
      <div className='case-page'>
        <div className='m-sec'>
          {items && (
            <>
              <CaseOpeningSec>
                <ItemLine items={items} />
              </CaseOpeningSec>
              <Showcase items={items} />
            </>
          )}
        </div>
      </div>
      <style jsx>
        {`
          .case-page {
            background-image: url("/images/csgomenubg.jpg");
            width: 100vw;
            height: 100vh;
            background-size: cover;
            background-repeat: no-repeat;
            display: flex;
            align-items: center;
            justify-content: center;
          }

          .case-page .m-sec {
            height: 70vh;
            width: 70vw;
          }
        `}
      </style>
    </>
  );
};

export default Case;
