import React, { Component } from "react";
import styles from "./ReceivedItems.module.scss";
import { itemType } from "../../helpers/item-type";
import Item from "../Item/Item";
import memoize from "memoize-one";

export default class ReceivedItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: this.select(this.props.page),
    };
  }
  getDerivedData = memoize(computeDerivedState);
  // static getDerivedStateFromProps(props, state) {
  //   return {
  //     items: ReceivedItems.select(props.page),
  //   };
  // }

  select = (page) => {
    let allItems = JSON.parse(localStorage.getItem("receivedItems"));
    let firstIndex = (page - 1) * 30;
    let lastIndex = firstIndex + 29;
    console.log(allItems.slice(firstIndex, lastIndex));
    return allItems.slice(firstIndex, lastIndex);
  };

  render() {
    const derivedData = this.getDerivedData(this.props.page);
    console.log(derivedData);
    return (
      <div className={styles.receivedItems}>
        {this.state.items.map((value, index) => (
          <Item
            key={index}
            showSkinTitle
            type={value.type}
            image={value.image}
            weapon={value.weapon}
            skin={value.skin}
          />
        ))}
      </div>
    );
  }
}
