import React, { useEffect, useState } from "react";
import { UnlockingContainerNotification } from "../UnlockingContainerNotification/UnlockingContainerNotification";

export const CaseOpeningSec = props => {
  const [line, setLine] = useState(null);
  const [openingInProcess, setOpeningInProcess] = useState(false);
  useEffect(() => {
    if (items) {
      setLine(generateLine());
    }
  }, [items]);
  const generateLine = () => {
    let elements = [];
    const actItem = selectRandomItem(getRandom());
    for (let index = 0; index < 30; index++) {
      if (index === 25) {
        elements.push(actItem);
      } else {
        const randomType = getRandom();
        const item = selectRandomItem(randomType);
        elements.push(item);
      }
    }
    return elements;
  };
  const getRandom = () => {
    const ran = Math.random();
    console.log(ran);
    if (ran < ActualItemChance.BLUE) return 1;
    else if (ran < ActualItemChance.PURPLE) return 2;
    else if (ran < ActualItemChance.PINK) return 3;
    else if (ran < ActualItemChance.RED) return 4;
    else return 5;
  };

  const selectRandomItem = type => {
    const getRandomInt = (min, max) => {
      min = Math.ceil(min);
      max = Math.floor(max);
      return Math.floor(Math.random() * (max - min)) + min;
    };
    if (type === 5) {
      const ran = getRandomInt(0, specialItems.length);
      return specialItems[ran];
    } else {
      const allGroupItems = [...items.filter(val => val.type === type)];
      const ran = getRandomInt(0, allGroupItems.length);
      return allGroupItems[ran];
    }
  };
  return (
    <>
      <div className='case-opening-sec'>
        <div className='head-wrap'>
          <div className='case-opener-bg'>
            <hr className='middle-line' />
            <div className='case-opener'>
              <ItemLine items={line} />
            </div>
          </div>
        </div>
        <UnlockingContainerNotification />
      </div>

      <style jsx>
        {`
          .case-opening-sec {
            width: 100%;
            height: 57%;
            padding-top: 2rem;
            background: #121b24;
            box-shadow: 0px 0px 11px 1px rgba(0, 0, 0, 0.75);
          }

          .case-opening-sec .head-wrap {
            width: 102%;
            transform: translateX(-1%);
            background: #10161b;
            box-shadow: 0px 0px 11px 1px rgba(0, 0, 0, 0.75);
            height: 54%;
            display: flex;
            align-items: center;
            justify-content: center;
          }

          .case-opening-sec .head-wrap .case-opener-bg {
            width: 45%;
            background: black;
            position: relative;
            padding: 16px 2px;
          }

          .case-opening-sec .head-wrap .case-opener-bg .case-opener {
            width: 100%;
            height: 102px;
            overflow: hidden;
            position: relative;
          }

          .middle-line {
            background: #605337;
            height: 100%;
            position: absolute;
            left: 50%;
            width: 3px;
            transform: translateX(-50%);
            top: -16px;
            z-index: 1;
          }
        `}
      </style>
    </>
  );
};
