import React, { Component } from "react";
import styles from "./ReceivedItems.module.scss";
import { itemType } from "../../helpers/item-type";
import Item from "../Item/Item";
import memoize from "memoize-one";
import Storage from "../../helpers/Storage";
import ScrollableList from "../ScrollableList/ScrollableList";

export default class ReceivedItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: this.select(),
    };
  }

  // componentDidUpdate(oldProps) {
  //   const newProps = this.props;
  //   if (oldProps.page !== newProps.page) {
  //     const val = this.select(newProps.page);
  //     this.setState({
  //       items: val,
  //     });
  //   }
  // }

  select = () => {
    return Storage.selectAll();

    // let firstIndex = (page - 1) * 24;
    // let lastIndex = firstIndex + 24;
    // return allItems.slice(firstIndex, lastIndex);
  };

  render() {
    return (
      <ScrollableList>
        {this.state.items.map((value, index) => (
          <Item
            key={this.props.page * 24 + index}
            showSkinTitle
            inventoryStyles
            type={value.type}
            image={value.image}
            weapon={value.weapon}
            skin={value.skin}
          />
        ))}
      </ScrollableList>
    );
  }
}
