import { useRouter } from "next/router";
import React, { useEffect, useState } from "react";
import { CaseOpeningSec } from "../../Components/CaseOpeningSec/CaseOpeningSec";
import Showcase from "../../Components/Showcase/Showcase";
import souvenirs from "../../public/souvenir.json";
import { Layout } from "../../Components/Layout/Layout";
import { containerType } from "../../helpers/container-type";

const Souvenir = () => {
  const router = useRouter();
  const [souvenirInfo, setSouvenirInfo] = useState(null);
  const [items, setItems] = useState(null);

  useEffect(() => {
    if (router && router.query && router.query.id) {
      const { title, image, items } = souvenirs.find(
        (value) => value.id === +router.query.id
      );
      setSouvenirInfo({
        title: title,
        image: image,
      });
      setItems(items);
    }
  }, [router]);

  return (
    <Layout
      content={
        <div className='souvenir-page'>
          <div className='m-sec'>
            {items && (
              <>
                <div className='head-wrap'></div>
                <CaseOpeningSec
                  items={items}
                  containerType={containerType[1]}
                  caseTitle={souvenirInfo.title}
                  caseImage={souvenirInfo.image}
                />
                <Showcase isSouvenir items={items} />
              </>
            )}
          </div>
          <style jsx>
            {`
              .souvenir-page {
                display: flex;
                align-items: center;
                justify-content: center;
              }

              .head-wrap {
                background: #121b24;
              }

              .souvenir-page .m-sec {
                height: 85vh;
                width: 70vw;
              }
            `}
          </style>
        </div>
      }
    />
  );
};

export default Souvenir;
