import React, { Component } from "react";
import Item from "../Item/Item";
import styles from "./ItemLine.module.scss";
import { getRandomInt } from "../../helpers/random";

export default class ItemLine extends Component {
  render() {
    return (
      <div className='item-line'>
        {this.props.items.map((value, index) => {
          if (value.type === 5) {
            return (
              <Item
                key={index}
                type={5}
                image={"/images/specialitem1.png"}
                weapon={"★ Rare special item ★"}
              />
            );
          } else {
            return (
              <Item
                key={index}
                type={value.type}
                image={value.image}
                showSkinTitle
                weapon={value.weapon}
                skin={value.skin}
              />
            );
          }
        })}
        <styles jsx>
          {`
            .item-line {
              display: flex;
              animation: lineanim 8s;
              animation-fill-mode: forwards;
            
              & > div {
                margin-left: 5px;
              }
            }
            
            @keyframes lineanim {
              0% {
                transform: translateX(0);
              }
            
              100% {
                transform: translateX(-#{${getRandomInt(2300, 2399)}}px);
              }
            }
          `}
        </styles>
      </div>
    );
  }
}
