import React from "react";
import CaseList from "../Components/CaseList/CaseList";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      chosenSection: 1
    };
  }

  render() {
    return (
      <>
        <div className='index'>
          <div className='inventory'>
            <div className='header'>
              <button
                onClick={() => {
                  this.setState({
                    chosenSection: 1
                  });
                }}
              >
                Cases
              </button>
              <button
                onClick={() => {
                  this.setState({
                    chosenSection: 2
                  });
                }}
              >
                Items
              </button>
            </div>
            {this.state.chosenSection === 1 && <CaseList />}
          </div>
        </div>
        <style jsx>
          {`
            .index {
              background-image: url("/images/csgomenubg.jpg");
              width: 100vw;
              height: 100vh;
              background-size: cover;
              background-repeat: no-repeat;
            }
            .inventory {
              height: 70vh;
              width: 70vw;
            }
          `}
        </style>
      </>
    );
  }
}

export default App;
