import React, { Component } from "react";
import Item from "../Item/Item";
import styles from "./ItemLine.module.scss";
import styled, { css } from "styled-components";
import { slider } from "./keyframes";
import Env from "../../helpers/env";

export default class ItemLine extends Component {
  Animation;
  sub;
  constructor() {
    super();
    this.sub = Env.caseOpeningTime$.subscribe((value) => {
      Animation = styled.div`
        display: flex;
        animation: ${css`
          ${slider} ${value / 1000}s;
        `};
        animation-fill-mode: forwards;

        & > div {
          margin-left: 5px;
        }
      `;
    });
  }

  componentWillUnmount() {
    this.sub.unsubscribe();
  }

  render() {
    return (
      <Animation>
        {this.props.items.map((value, index) => {
          if (value.type === 5) {
            return (
              <Item
                key={index}
                type={5}
                image={"/images/specialitem1.png"}
                isStatrak={value.isStatrak}
                weapon={"★ Rare special item ★"}
              />
            );
          } else {
            return (
              <Item
                key={index}
                type={value.type}
                image={value.image}
                showSkinTitle
                isStatrak={value.isStatrak}
                weapon={value.weapon}
                skin={value.skin}
              />
            );
          }
        })}
      </Animation>
    );
  }
}
