import React, { Component } from "react";
import styles from "./ReceivedItems.module.scss";
import { itemType } from "../../helpers/item-type";

export default class ReceivedItems extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: this.select(this.props.page),
    };
  }

  select = (page) => {
    const keyByValue = (value) => {
      return Object.keys(itemType)[Object.values(itemType).indexOf(value)];
    };
    let allItems = localStorage.getItem("receivedItems");
    allItems.map((value) => {
      value.type = keyByValue(value.type);
    });
    let firstIndex = (page - 1) * 30;
    let lastIndex = firstIndex + 29;
    return allItems.slice(firstIndex, lastIndex);
  };

  render() {
    return (
      <div className={styles.receivedItems}>
        {this.state.items.map((value, index) => (
          <Item
            key={index}
            showSkinTitle
            type={value.type}
            image={value.image}
            weapon={value.weapon}
            skin={value.skin}
          />
        ))}
      </div>
    );
  }
}
