import React, { Component } from "react";
import Item from "../Item/Item";
import styled, { css } from "styled-components";
import { slider } from "./keyframes";
import Env from "../../helpers/env";
import { itemType } from "../../helpers/item-type";

export default class ItemLine extends Component {
  Animation;
  sub;
  constructor() {
    super();
    this.sub = Env.caseOpeningTime$.subscribe((value) => {
      Animation = styled.div`
        display: flex;
        animation: ${css`
          ${slider} ${value / 1000}s;
        `};
        animation-fill-mode: forwards;

        & > div {
          margin-left: 5px;
        }
      `;
    });
  }

  componentWillUnmount() {
    this.sub.unsubscribe();
  }

  render() {
    return (
      <Animation>
        {this.props.items.map((value, index) => {
          if (value.type === itemType.GOLD) {
            return (
              <Item
                key={index}
                type={itemType.GOLD}
                image={"/images/specialitem1.png"}
                isStatrak={value.isStatrak}
                weapon={"★ Rare Special Item ★"}
              />
            );
          } else {
            return (
              <Item
                key={index}
                type={value.type}
                image={value.image}
                showSkinTitle
                isStatrak={value.isStatrak}
                weapon={value.weapon}
                skin={value.skin}
              />
            );
          }
        })}
      </Animation>
    );
  }
}
