import React, { Component } from "react";
import Item from "../Item/Item";
import styles from "./ItemLine.module.scss";
import { getRandomInt } from "../../helpers/random";
import styled, { keyframes, css } from "styled-components";

export const ItemLine = (props) => {
  let anim = keyframes`
  0% {
    transform: translateX(0);
  }

  100% {
    transform: translateX(-#{${getRandomInt(2300, 2399)}}px);
  }`;

  const animation = () => {
    return css`
      ${anim} 8s;
    `;
  };

  return (
    <div style={{ animation }} className={styles.itemLine}>
      {props.items.map((value, index) => {
        if (value.type === 5) {
          return (
            <Item
              key={index}
              type={5}
              image={"/images/specialitem1.png"}
              weapon={"★ Rare special item ★"}
            />
          );
        } else {
          return (
            <Item
              key={index}
              type={value.type}
              image={value.image}
              showSkinTitle
              weapon={value.weapon}
              skin={value.skin}
            />
          );
        }
      })}
    </div>
  );
};
