import React, { Component } from "react";
import Item from "../Item/Item";
import styles from "./ItemLine.module.scss";
import styled from "styled-components";
import { slider } from "./keyframes";

const anim = `
  ${slider} 8s;
`;

const Animation = styled.div`
  animation: ${anim};
  animation-fill-mode: forwards;

  & > div {
    margin-left: 5px;
    float: left;
  }
`;

export const ItemLine = (props) => {
  return (
    <Animation>
      {props.items.map((value, index) => {
        if (value.type === 5) {
          return (
            <Item
              key={index}
              type={5}
              image={"/images/specialitem1.png"}
              weapon={"★ Rare special item ★"}
            />
          );
        } else {
          return (
            <Item
              key={index}
              type={value.type}
              image={value.image}
              showSkinTitle
              weapon={value.weapon}
              skin={value.skin}
            />
          );
        }
      })}
    </Animation>
  );
};
