import React, { Component } from "react";
import CaseList from "../CaseList/CaseList";
import styles from "./Inventory.module.scss";
import { InventoryButton } from "../InventoryButton/InventoryButton";
import { Dropdown } from "../Dropdown/Dropdown";
import { chosenContainerType } from "../../helpers/chosen-container-type";

export default class Inventory extends Component {
  constructor(props) {
    super(props);
    this.state = {
      chosenSection: 1,
      currentPage: 1,
      chosenContainerType: chosenContainerType[0],
    };
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.chosenSection !== this.state.chosenSection) {
      this.setState({
        currentPage: 1,
      });
    }
  }

  render() {
    return (
      <div className={styles.inventory}>
        <div className={styles.header}>
          <InventoryButton
            onClick={() => {
              this.setState({
                chosenSection: 1,
              });
            }}
            active={this.state.chosenSection === 1}
            text='Cases'
          />
          <InventoryButton
            onClick={() => {
              this.setState({
                chosenSection: 2,
              });
            }}
            active={this.state.chosenSection === 2}
            text='Items'
          />
        </div>
        <div className={styles.ddWrap}>
          <Dropdown
            title='Type:'
            options={[...chosenContainerType.map((value) => value.title)]}
            onOptionClick={(index) => {
              this.setState({
                chosenContainerType: chosenContainerType[index],
              });
            }}
            chosenOption={this.state.chosenContainerType.title}
          />
        </div>
        <div className={styles.listWrap}>
          {this.state.chosenSection === 1 && (
            <CaseList page={this.state.currentPage} />
          )}
        </div>
        <button className={styles.nextPageBtn}>
          <img src='/images/chevron-right.png' alt='' />
        </button>
      </div>
    );
  }
}
