import React, { useState, useEffect } from "react";
import { useRouter } from "next/router";
import ItemLine from "../../Components/ItemLine/ItemLine";
import Showcase from "../../Components/Showcase/Showcase";
import { CaseOpeningSec } from "../../Components/CaseOpeningSec/CaseOpeningSec";
import cases from "../../public/cases.json";
import { ActualItemChance } from "../../helpers/chance.js";
import CaseOpeningConfirmation from "../../Components/CaseOpeningConfirmation/CaseOpeningConfirmation";

const Case = () => {
  const router = useRouter();
  const [caseInfo, setCaseInfo] = useState(null);
  const [items, setItems] = useState(null);
  const [specialItems, setSpecialItems] = useState(null);

  useEffect(() => {
    if (router && router.query && router.query.id) {
      const { title, image, items, specialItems } = cases.find(
        value => value.id === +router.query.id
      );
      setCaseInfo({
        title: title,
        image: image
      });
      setItems(items.reverse());
      setSpecialItems(specialItems);
    }
  }, [router]);

  return (
    <>
      <div className='case-page'>
        <div className='m-sec'>
          {items && (
            <>
              <div className='head-wrap'></div>
              <CaseOpeningSec items={items} specialItems={specialItems}>
                {/* <CaseOpeningConfirmation
                  image={caseInfo.image}
                  title={caseInfo.title}
                  onSubmit={setOpeningInProcess(true)}
                />
                {openingInProcess && <ItemLine items={line} />} */}
              </CaseOpeningSec>
              <Showcase items={items} />
            </>
          )}
        </div>
      </div>
      <style jsx>
        {`
          .case-page {
            background-image: url("/images/csgomenubg.jpg");
            width: 100vw;
            height: 100vh;
            background-size: cover;
            background-repeat: no-repeat;
            display: flex;
            align-items: center;
            justify-content: center;
          }

          .head-wrap {
            background: #121b24;
          }

          .case-page .m-sec {
            height: 70vh;
            width: 70vw;
          }
        `}
      </style>
    </>
  );
};

export default Case;
