import React, { useState, useEffect } from "react";
import { useRouter } from "next/router";
import ItemLine from "../../Components/ItemLine/ItemLine";
import Showcase from "../../Components/Showcase/Showcase";
import CaseOpeningSec from "../../Components/CaseOpeningSec/CaseOpeningSec";
import cases from "../../public/cases.json";
import { ActualItemChance } from "../../helpers/chance.js";
import { AnimPartItemChance } from "../../helpers/chance.js";

const Case = () => {
  const router = useRouter();
  const [items, setItems] = useState(null);
  const [specialItems, setSpecialItems] = useState(null);
  const [line, setLine] = useState(null);

  const getRandom = () => {
    const ran = Math.random();
    if (ran < ActualItemChance.BLUE) return 1;
    else if (ran < ActualItemChance.PURPLE) return 2;
    else if (ran < ActualItemChance.PINK) return 3;
    else if (ran < ActualItemChance.RED) return 4;
    else return 5;
  };

  // const getRandomAnimPart = () => {
  //   const ran = Math.random();
  //   console.log(ran);
  //   if (ran < AnimPartItemChance.BLUE) return 1;
  //   else if (ran < AnimPartItemChance.PURPLE) return 2;
  //   else if (ran < AnimPartItemChance.PINK) return 3;
  //   else if (ran < AnimPartItemChance.RED) return 4;
  //   else return 5;
  // };

  const selectRandomItem = type => {
    const getRandomInt = (min, max) => {
      min = Math.ceil(min);
      max = Math.floor(max);
      return Math.floor(Math.random() * (max - min)) + min;
    };
    if (type === 5) {
      const ran = getRandomInt(0, specialItems.length);
      return specialItems[ran];
    } else {
      const allGroupItems = [...items.filter(val => val.type === type)];
      const ran = getRandomInt(0, allGroupItems.length);
      return allGroupItems[ran];
    }
  };

  const generateLine = () => {
    let elements = [];
    const actItem = selectRandomItem(getRandom());
    for (let index = 0; index < 50; index++) {
      if (index === 30) {
        elements.push(actItem);
      } else {
        const item = selectRandomItem(getRandom());
        elements.push(item);
      }
    }
    return elements;
  };

  useEffect(() => {
    if (router && router.query && router.query.id) {
      setItems(cases.find(value => value.id === +router.query.id).items);
      setSpecialItems(
        cases.find(value => value.id === +router.query.id).specialItems
      );
    }
  }, [router]);

  useEffect(() => {
    if (items) {
      setLine(generateLine());
    }
  }, [items]);

  return (
    <>
      <div className='case-page'>
        <div className='m-sec'>
          {items && line && (
            <>
              <div className='head-wrap'></div>
              <CaseOpeningSec>
                <ItemLine items={line} />
              </CaseOpeningSec>
              <Showcase items={items} />
            </>
          )}
        </div>
      </div>
      <style jsx>
        {`
          .case-page {
            background-image: url("/images/csgomenubg.jpg");
            width: 100vw;
            height: 100vh;
            background-size: cover;
            background-repeat: no-repeat;
            display: flex;
            align-items: center;
            justify-content: center;
          }

          .head-wrap {
            background: #121b24;
          }

          .case-page .m-sec {
            height: 70vh;
            width: 70vw;
          }
        `}
      </style>
    </>
  );
};

export default Case;
