import React, { Component } from "react";
import cases from "../../public/cases.json";
import Case from "../Case/Case";

export default class CaseList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      caseList: this.select(1)
    };
  }

  select = page => {
    let firstIndex = (page - 1) * 30;
    let lastIndex = firstIndex + 29;
    return cases.slice(firstIndex, lastIndex);
  };

  render() {
    return (
      <div className='case-list'>
        {this.state.caseList.map((value, index) => (
          <Case
            key={index}
            id={value.id}
            title={value.title}
            img={value.image}
          />
        ))}
        <style jsx>
          {`
            .case-list {
              display: grid;
              grid-template-columns: repeat(6, 100px);
              grid-gap: 15px;
              justify-content: center;
              align-content: start;
            }
          `}
        </style>
      </div>
    );
  }
}
