import { useRouter } from "next/router";
import React, { useEffect, useState } from "react";
import { CaseOpeningSec } from "../../Components/CaseOpeningSec/CaseOpeningSec";
import Showcase from "../../Components/Showcase/Showcase";
import cases from "../../public/cases.json";
import { Layout } from "../../Components/Layout/Layout";

const Case = () => {
  const router = useRouter();
  const [caseInfo, setCaseInfo] = useState(null);
  const [items, setItems] = useState(null);
  const [specialItems, setSpecialItems] = useState(null);

  useEffect(() => {
    if (router && router.query && router.query.id) {
      const { title, image, items, specialItems } = cases.find(
        (value) => value.id === +router.query.id
      );
      setCaseInfo({
        title: title,
        image: image,
      });
      setItems(items);
      setSpecialItems(specialItems);
    }
  }, [router]);

  return (
    <Layout
      content={
        <div className='case-page'>
          <div className='m-sec'>
            {items && specialItems && (
              <>
                <div className='head-wrap'></div>
                <CaseOpeningSec
                  items={items}
                  caseTitle={caseInfo.title}
                  caseImage={caseInfo.image}
                  specialItems={specialItems}
                />
                <Showcase items={items} />
              </>
            )}
          </div>
          <style jsx>
            {`
              .case-page {
                display: flex;
                align-items: center;
                justify-content: center;
              }

              .head-wrap {
                background: #121b24;
              }

              .case-page .m-sec {
                height: 85vh;
                width: 70vw;
              }
            `}
          </style>
        </div>
      }
    />
  );
};

export default Case;
