import { useRouter } from "next/router";
import React, { useContext, useState } from "react";
import { SettingsContext } from "../../contexts/SettingsContext";
import {
  ItemChance,
  ItemStatrakChance,
  QualityChance,
} from "../../helpers/chance";
import { containerType } from "../../helpers/container-type";
import { itemType } from "../../helpers/item-type";
import { getRandomInt } from "../../helpers/random";
import Storage from "../../helpers/storage";
import CaseOpeningAudio from "../CaseOpeningAudio/CaseOpeningAudio";
import { CaseOpeningConfirmation } from "../CaseOpeningConfirmation/CaseOpeningConfirmation";
import ItemLine from "../ItemLine/ItemLine";
import ReceivedItemModal from "../ReceivedItemModal/ReceivedItemModal";
import { UnlockingContainerNotification } from "../UnlockingContainerNotification/UnlockingContainerNotification";
import { Quality } from "../../helpers/quality";
import RandomItem from "./RandomItem";
import RandomSouvenir from "./RandomSouvenir";
import RandomCaseItem from "./RandomCaseItem";

export const CaseOpeningSec = (props) => {
  const context = useContext(SettingsContext);
  const [line, setLine] = useState(null);
  const [openingInProgress, setOpeningInProgress] = useState(false);
  const [receivedItemModalVisible, setReceivedItemModalVisible] = useState(
    false
  );
  const [receivedItem, setReceivedItem] = useState(null);
  const router = useRouter();

  const generateItem = () => {
    let item;
    switch (props.containerType) {
      case containerType[0]:
        item = selectRandomItem();
        break;
      case containerType[1]:
        item = selectRandomSouvenir();
        break;
      default:
        item = selectRandomItem();
        break;
    }
    return item;
  };

  const generateLine = () => {
    let elements = [];
    let actItem = generateItem();
    setReceivedItem(actItem);
    Storage.saveReceivedItem(actItem);
    for (let index = 0; index < 30; index++) {
      if (index === 25) {
        elements.push(actItem);
      } else {
        elements.push(generateItem());
      }
    }
    return <ItemLine items={elements} />;
  };

  const getRandomSouvenir = () => {
    const randomSouvenir = new RandomSouvenir();

    // const ran = Math.random();
    // if (
    //   props.items.some((e) => e.type == itemType.GREY) &&
    //   ran < ItemChance.GREY
    // )
    //   return itemType.GREY;
    // else if (
    //   props.items.some((e) => e.type == itemType.LIGHTBLUE) &&
    //   ran < ItemChance.LIGHTBLUE
    // )
    //   return itemType.LIGHTBLUE;
    // else if (
    //   props.items.some((e) => e.type == itemType.BLUE) &&
    //   ran < ItemChance.BLUE
    // )
    //   return itemType.BLUE;
    // else if (
    //   props.items.some((e) => e.type == itemType.PURPLE) &&
    //   ran < ItemChance.PURPLE
    // )
    //   return itemType.PURPLE;
    // else if (
    //   props.items.some((e) => e.type == itemType.PINK) &&
    //   ran < ItemChance.PINK
    // )
    //   return itemType.PINK;
    // else if (
    //   props.items.some((e) => e.type == itemType.RED) &&
    //   ran < ItemChance.RED
    // )
    //   return itemType.RED;
    // else if (
    //   props.items.some((e) => e.type == itemType.GOLD) &&
    //   ran < ItemChance.GOLD
    // )
    //   return itemType.GOLD;
  };

  // const getRandom = () => {
  //   const ran = Math.random();
  //   if (ran < ItemChance.BLUE) return itemType.BLUE;
  //   else if (ran < ItemChance.PURPLE) return itemType.PURPLE;
  //   else if (ran < ItemChance.PINK) return itemType.PINK;
  //   else if (ran < ItemChance.RED) return itemType.RED;
  //   else if (ran < ItemChance.GOLD) return itemType.GOLD;
  //   else return itemType.BLUE;
  // };

  // const getRandomStatrak = (type) => {
  //   const ran = Math.random();
  //   if (ran < ItemStatrakChance.BLUE && type == itemType.BLUE) return true;
  //   else if (ran < ItemStatrakChance.PURPLE && type == itemType.PURPLE)
  //     return true;
  //   else if (ran < ItemStatrakChance.PINK && type == itemType.PINK) return true;
  //   else if (ran < ItemStatrakChance.RED && type == itemType.RED) return true;
  //   else if (ran < ItemStatrakChance.GOLD && type == itemType.GOLD) return true;
  //   else return false;
  // };

  // const getRandomQuality = (item) => {
  //   const ran = Math.random();
  //   if (item.quality == null) {
  //     if (ran < QualityChance.BATTLESCARRED) return Quality["Battle-Scarred"];
  //     else if (ran < QualityChance.FIELDTESTED) return Quality["Field-Tested"];
  //     else if (ran < QualityChance.WELLWORN) return Quality["Well-Worn"];
  //     else if (ran < QualityChance.MINIMALWEAR) return Quality["Minimal Wear"];
  //     else if (ran < QualityChance.FACTORYNEW) return Quality["Factory New"];
  //     else return Quality["Battle-Scarred"];
  //   } else {
  //     if (
  //       item.quality.indexOf(Quality["Battle-Scarred"]) !== -1 &&
  //       ran < QualityChance.BATTLESCARRED
  //     )
  //       return Quality["Battle-Scarred"];
  //     else if (
  //       item.quality.indexOf(Quality["Field-Tested"]) !== -1 &&
  //       ran < QualityChance.FIELDTESTED
  //     )
  //       return Quality["Field-Tested"];
  //     else if (
  //       item.quality.indexOf(Quality["Well-Worn"]) !== -1 &&
  //       ran < QualityChance.WELLWORN
  //     )
  //       return Quality["Well-Worn"];
  //     else if (
  //       item.quality.indexOf(Quality["Minimal Wear"]) !== -1 &&
  //       ran < QualityChance.MINIMALWEAR
  //     )
  //       return Quality["Minimal Wear"];
  //     else if (
  //       item.quality.indexOf(Quality["Factory New"]) !== -1 &&
  //       ran < QualityChance.FACTORYNEW
  //     )
  //       return Quality["Factory New"];
  //     else return Quality["Battle-Scarred"];
  //   }
  // };

  const selectRandomSouvenir = () => {
    let randomItem = new RandomItem();
    let randomSouvenir = new RandomSouvenir();
    const type = randomSouvenir.getRandomSouvenirTypeByItems(props.items);
    const item = randomItem.getSouvenirItemByType(props.items, type);
    return item;
  };
  const selectRandomItem = () => {
    let randomItem = new RandomItem();
    let randomCaseItem = new RandomCaseItem();
    const type = randomCaseItem.getRandomType();
    const item = randomItem.getItemFromCaseByType(
      props.items,
      props.specialItems,
      type
    );
    return item;

    // let isStatrak = false;
    // if (!props.isSouvenier && !props.isSticker) {
    //   isStatrak = getRandomStatrak(type);
    // }
    // if (type === itemType.GOLD) {
    //   const ran = getRandomInt(0, props.specialItems.length);
    //   const item = props.specialItems[ran];
    //   const quality = getRandomQuality(item);
    //   return {
    //     ...item,
    //     isStatrak,
    //     quality,
    //   };
    // } else {
    //   const allGroupItems = [...props.items.filter((val) => val.type === type)];
    //   const item = allGroupItems[ran];
    //   const ran = getRandomInt(0, allGroupItems.length);
    //   const quality = getRandomQuality(item);
    //   return { ...item, isStatrak, quality };
    // }
  };
  const openCase = () => {
    setLine(generateLine());
    setOpeningInProgress(true);
    setTimeout(() => {
      setReceivedItemModalVisible(true);
    }, context.caseOpeningTime + 100);
  };
  const onReceivedItemConfirmation = () => {
    router.push("/");
  };
  const onTryAgainBtnClick = () => {
    setReceivedItemModalVisible(false);
    setOpeningInProgress(false);
  };
  return (
    <>
      <div className='case-opening-sec'>
        {openingInProgress && <CaseOpeningAudio />}
        {receivedItemModalVisible && (
          <ReceivedItemModal
            title={receivedItem.title}
            isStatrak={receivedItem.isStatrak}
            quality={receivedItem.quality}
            type={receivedItem.type}
            image={receivedItem.image}
            onTryAgainBtnClick={() => onTryAgainBtnClick()}
            onSubmit={() => onReceivedItemConfirmation()}
          />
        )}

        <div className='head-wrap'>
          {!openingInProgress && (
            <CaseOpeningConfirmation
              image={props.caseImage}
              title={props.caseTitle}
              onSubmit={() => openCase()}
            />
          )}
          {openingInProgress && (
            <>
              <div className='case-opener-bg'>
                <hr className='middle-line' />
                <div className='case-opener'>{line}</div>
              </div>
            </>
          )}
        </div>
        {openingInProgress && <UnlockingContainerNotification />}
      </div>

      <style jsx>
        {`
          .case-opening-sec {
            width: 100%;
            height: 50%;
            padding-top: 2rem;
            background: #121b24;
            box-shadow: 0px 0px 11px 1px rgba(0, 0, 0, 0.75);
          }

          .case-opening-sec .head-wrap {
            width: 101%;
            transform: translateX(-0.5%);
            background: #10161b;
            box-shadow: 0px 0px 11px 1px rgba(0, 0, 0, 0.75);
            height: 54%;
            display: flex;
            align-items: center;
            justify-content: center;
          }

          .case-opening-sec .head-wrap .case-opener-bg {
            width: 400px;
            background: black;
            position: relative;
            padding: 16px 2px;
          }

          .case-opening-sec .head-wrap .case-opener-bg .case-opener {
            width: 100%;
            height: 102px;
            overflow: hidden;
            position: relative;
          }

          .middle-line {
            background: #605337;
            height: 100%;
            position: absolute;
            left: 50%;
            width: 3px;
            transform: translateX(-50%);
            top: 0;
            margin: 0;
            z-index: 1;
            border: none;
          }
        `}
      </style>
    </>
  );
};
