import React, { Component } from "react";
import "./item-card.module.scss";

export default class ItemCard extends Component {
  componentDidMount() {
    switch (this.props.type) {
      case 1:
        this.setState({
          gunCardBorderName: "gun-card--blue"
        });
        break;
      case 2:
        this.setState({
          gunCardBorderName: "gun-card--purple"
        });
        break;
      case 3:
        this.setState({
          gunCardBorderName: "gun-card--pink"
        });
        break;
      case 4:
        this.setState({
          gunCardBorderName: "gun-card--red"
        });
        break;
      default:
        break;
    }
  }
  render() {
    return (
      <div className={`gun-card ${this.state.gunCardBorderName}`}>
        <img src={this.props.image} alt='' />
      </div>
    );
  }
}
